    }
  }

  render() {
    return (
      <button>
        <PDFDownloadLink document={<Resume />} fileName="resume.pdf">
          {({ blob, url, loading, error }) =>
        loading ? 'Loading document...' : 'Download now!'
      }
    </PDFDownloadLink>
      </button>
    )
  }
}

export default Download;
