  render() {
    return (
      <button type="button" className={styles['toggle']} onClick={this.props.handle}>
        <img src={this.props.mobile.formIsOpen ? require("../../icons/preview.png") : require("../../icons/editor.png")}></img>
        {this.props.mobile.formIsOpen ? "Preview" : "Editor"}
      </button>
