  }

  render() {
    return (
      <div className={styles['body']}>
        <div className={styles['App']}>
          <Form experience={this.state.experience} skills={this.state.skills} addWork={this.addWork} />
          <Preview />
        </div>
      </div>
    )
  }
}

export default App;
